import React from "react";
import "./Grandpa.css";
import Parent from "./Components/Parent/Parent";

class App extends React.Component {
  constructor() {
    super();

    this.hideComponent = this.hideComponent.bind(this);
    this.state = { hideChildComponent: false };
  }

  hideComponent() {
    this.setState({ hideChildComponent: true });
  }

  render() {
    return (
      <div className="App">
        Grandpa Component
        {!this.state.hideChildComponent && <Parent intialCount={10} />}
      </div>
    );
  }
}

export default App;
