import React from "react";
import "./Grandpa.css";
import Parent from "./Components/Parent/Parent";

class App extends React.Component {
  constructor() {
    this.state = { myValue: 10 };
  }
  render() {
    return (
      <div className="Grandpa">
        Grandpa Component
        <Parent value={this.state.myValue} />
      </div>
    );
  }
}

export default App;
